import{_ as a,o as i,c as p,ag as e}from"./chunks/framework.ez65dQvE.js";const s=JSON.parse('{"title":"建筑与核心升级策略","description":"","frontmatter":{},"headers":[],"relativePath":"SimplifiedChinese/发育/3.建筑与核心升级策略.md","filePath":"SimplifiedChinese/发育/3.建筑与核心升级策略.md"}'),o={name:"SimplifiedChinese/发育/3.建筑与核心升级策略.md"};function r(t,l,h,u,n,c){return i(),p("div",null,[...l[0]||(l[0]=[e('<h1 id="建筑与核心升级策略" tabindex="-1">建筑与核心升级策略 <a class="header-anchor" href="#建筑与核心升级策略" aria-label="Permalink to &quot;建筑与核心升级策略&quot;">​</a></h1><p>建筑系统是发育的主轴。</p><p>错误做法：</p><blockquote><p>所有建筑平均升级。</p></blockquote><p>正确做法：</p><blockquote><p>围绕核心推进，其他为辅助。</p></blockquote><hr><h2 id="一、核心优先原则" tabindex="-1">一、核心优先原则 <a class="header-anchor" href="#一、核心优先原则" aria-label="Permalink to &quot;一、核心优先原则&quot;">​</a></h2><p>位置：</p><p>右下角 → 建筑图标</p><p>核心等级决定：</p><ul><li>解锁内容</li><li>发育上限</li><li>部分系统权限</li></ul><hr><h3 id="粗暴但有效的策略" tabindex="-1">粗暴但有效的策略 <a class="header-anchor" href="#粗暴但有效的策略" aria-label="Permalink to &quot;粗暴但有效的策略&quot;">​</a></h3><p>核心升级前：</p><ul><li>只升级“必须的前置建筑”</li><li>其他建筑一律不管</li></ul><p>核心升级完成后：</p><ul><li>再处理下一轮前置条件</li></ul><p>这种方式效率最高。</p><hr><h3 id="短时间升级策略" tabindex="-1">短时间升级策略 <a class="header-anchor" href="#短时间升级策略" aria-label="Permalink to &quot;短时间升级策略&quot;">​</a></h3><p>如果某个建筑：</p><ul><li>刚解锁</li><li>升级时间极短（1~5分钟）</li></ul><p>建议连续升级，直到：</p><p>升级时间达到 1~2 小时</p><p>然后停止，回归核心优先。</p><hr><h3 id="公会求助机制" tabindex="-1">公会求助机制 <a class="header-anchor" href="#公会求助机制" aria-label="Permalink to &quot;公会求助机制&quot;">​</a></h3><p>升级时可以点击“求助”。</p><p>公会成员可帮助缩短时间。</p><p>因此：</p><p>短时间建筑连续升级收益更高。</p><hr><h2 id="二、造船厂的特殊机制" tabindex="-1">二、造船厂的特殊机制 <a class="header-anchor" href="#二、造船厂的特殊机制" aria-label="Permalink to &quot;二、造船厂的特殊机制&quot;">​</a></h2><p>建筑最左侧三个：</p><ul><li>动能造船厂</li><li>光束造船厂</li><li>离子造船厂</li></ul><p>它们升级规则特殊：</p><blockquote><p>每 5 级一个质变。</p></blockquote><p>例如：</p><ul><li>24级 → 只能造 4阶船</li><li>25级 → 立刻解锁 5阶船</li></ul><p>属于阶梯式突破，而不是线性增长。</p><p>如果你接近关键等级：</p><p>可以优先冲刺。</p><hr><h2 id="三、资源生产建筑" tabindex="-1">三、资源生产建筑 <a class="header-anchor" href="#三、资源生产建筑" aria-label="Permalink to &quot;三、资源生产建筑&quot;">​</a></h2><p>包括：</p><ul><li>水循环系统</li><li>熔炼室</li><li>食品加工室</li></ul><p>适合：</p><ul><li>偏休闲玩家</li><li>每日上线 2~3 次领取资源</li></ul><p>活跃玩家：</p><p>不建议优先投资。</p><p>收益有限。</p><hr><h2 id="四、加速使用策略" tabindex="-1">四、加速使用策略 <a class="header-anchor" href="#四、加速使用策略" aria-label="Permalink to &quot;四、加速使用策略&quot;">​</a></h2><p>这是关键区别。</p><hr><h3 id="核心-15-级之前" tabindex="-1">核心 15 级之前 <a class="header-anchor" href="#核心-15-级之前" aria-label="Permalink to &quot;核心 15 级之前&quot;">​</a></h3><p>建议：</p><blockquote><p>大量使用加速。</p></blockquote><p>原因：</p><ul><li>升级时间短</li><li>加速收益高</li><li>发育节奏快</li></ul><p>萌新应优先冲等级。</p><hr><h3 id="核心-20-级之后" tabindex="-1">核心 20 级之后 <a class="header-anchor" href="#核心-20-级之后" aria-label="Permalink to &quot;核心 20 级之后&quot;">​</a></h3><p>升级时间：</p><ul><li>数天</li><li>甚至数周</li></ul><p>此时加速收益极低。</p><p>建议：</p><blockquote><p>囤积加速。</p></blockquote><p>留到：</p><ul><li>VS 活动</li><li>公会对决</li><li>冲榜活动</li></ul><p>玩法会发生转变：</p><p>前期 → 快速成长 后期 → 节奏对抗</p><hr><h2 id="五、隐藏收益-rian" tabindex="-1">五、隐藏收益：Rian <a class="header-anchor" href="#五、隐藏收益-rian" aria-label="Permalink to &quot;五、隐藏收益：Rian&quot;">​</a></h2><p>每天在核心建筑左下角：</p><p>可以与 NPC「Rian」对话。</p><p>奖励：</p><ul><li>50 行动值</li></ul><p>属于隐藏收益。</p><p>建议每天领取。</p><hr><h2 id="六、其他-npc-对话奖励" tabindex="-1">六、其他 NPC 对话奖励 <a class="header-anchor" href="#六、其他-npc-对话奖励" aria-label="Permalink to &quot;六、其他 NPC 对话奖励&quot;">​</a></h2><p>部分 NPC 会出现对话选项。</p><p>点击后可获得小额奖励。</p><p>建议顺手领取。</p><hr><h2 id="七、升级方向总结" tabindex="-1">七、升级方向总结 <a class="header-anchor" href="#七、升级方向总结" aria-label="Permalink to &quot;七、升级方向总结&quot;">​</a></h2><p>建筑升级没有分支路线。</p><p>只有三个问题：</p><ol><li>优先升谁</li><li>是否用加速</li><li>是否冲关键等级</li></ol><p>核心原则：</p><ul><li>核心优先</li><li>造船厂看阶梯</li><li>资源建筑看活跃度</li><li>加速分阶段使用</li></ul><hr><h2 id="小结" tabindex="-1">小结 <a class="header-anchor" href="#小结" aria-label="Permalink to &quot;小结&quot;">​</a></h2><p>建筑发育分两个阶段：</p><p>前期：</p><blockquote><p>快速推进核心</p></blockquote><p>后期：</p><blockquote><p>控制节奏，留资源打活动</p></blockquote><p>发育不是“全部升级”。</p><p>而是“选择性升级”。</p><p>平均主义是效率杀手。</p>',103)])])}const b=a(o,[["render",r]]);export{s as __pageData,b as default};
