import{_ as l,o as i,c as p,ag as e}from"./chunks/framework.ez65dQvE.js";const c=JSON.parse('{"title":"主动资源获取","description":"","frontmatter":{},"headers":[],"relativePath":"SimplifiedChinese/基础/4.主动资源获取与PvE.md","filePath":"SimplifiedChinese/基础/4.主动资源获取与PvE.md"}'),r={name:"SimplifiedChinese/基础/4.主动资源获取与PvE.md"};function o(t,a,h,n,u,d){return i(),p("div",null,[...a[0]||(a[0]=[e('<h1 id="主动资源获取" tabindex="-1">主动资源获取 <a class="header-anchor" href="#主动资源获取" aria-label="Permalink to &quot;主动资源获取&quot;">​</a></h1><p>主动获取资源，核心就是：</p><ul><li>打野怪</li><li>打玩家（PvP）</li></ul><p>本章主要讲 PvE 野怪部分。</p><hr><h2 id="一、不消耗行动值的野怪" tabindex="-1">一、不消耗行动值的野怪 <a class="header-anchor" href="#一、不消耗行动值的野怪" aria-label="Permalink to &quot;一、不消耗行动值的野怪&quot;">​</a></h2><h3 id="——-至高祈祷舰" tabindex="-1">—— 至高祈祷舰 <a class="header-anchor" href="#——-至高祈祷舰" aria-label="Permalink to &quot;—— 至高祈祷舰&quot;">​</a></h3><p>位置：</p><p>赎罪港湾（部分语言中可能叫“神社”）</p><p>名称： <strong>至高祈祷舰</strong></p><p>这是全图唯一一个：</p><blockquote><p>不消耗行动值的野怪。</p></blockquote><hr><h3 id="每日次数" tabindex="-1">每日次数 <a class="header-anchor" href="#每日次数" aria-label="Permalink to &quot;每日次数&quot;">​</a></h3><ul><li>普通玩家：3 次</li><li>黑金会员：4 次</li></ul><p>界面上会显示：</p><p>0/3 或 0/4</p><hr><h3 id="收益建议" tabindex="-1">收益建议 <a class="header-anchor" href="#收益建议" aria-label="Permalink to &quot;收益建议&quot;">​</a></h3><p>当前版本全图已开放。</p><p>原则：</p><blockquote><p>在能力范围内，尽量打最高等级。</p></blockquote><p>原因：</p><p>等级越高，掉落品质越好。</p><p>举例：</p><ul><li>140级 → 紫色碎片概率</li><li>150~160级 → 金色碎片概率明显提高</li></ul><p>此外还会掉落：</p><ul><li>舰船培养材料</li><li>其他强化资源</li></ul><hr><h3 id="额外奖励" tabindex="-1">额外奖励 <a class="header-anchor" href="#额外奖励" aria-label="Permalink to &quot;额外奖励&quot;">​</a></h3><p>每日打完后可获得：</p><ul><li>2500 公会币</li></ul><p>公会币可在商会商店消费。</p><p>因此：</p><blockquote><p>加入公会几乎是必选项。</p></blockquote><p>否则公会币无法有效利用。</p><hr><h2 id="二、消耗行动值的野怪" tabindex="-1">二、消耗行动值的野怪 <a class="header-anchor" href="#二、消耗行动值的野怪" aria-label="Permalink to &quot;二、消耗行动值的野怪&quot;">​</a></h2><p>行动值查看方式：</p><p>左上角头像下方绿色条 默认上限 300 每小时恢复 20 点（随等级变化）</p><hr><h3 id="行动值补充方式" tabindex="-1">行动值补充方式 <a class="header-anchor" href="#行动值补充方式" aria-label="Permalink to &quot;行动值补充方式&quot;">​</a></h3><p>右上角资源栏 → 点击行动值 → 加号</p><p>可使用行动药水：</p><ul><li>蓝色：+10</li><li>紫色：+100</li><li>金色：+200</li></ul><p>建议：</p><blockquote><p>平时不要大量消耗行动药水。</p></blockquote><p>活动期间使用收益更高。</p><p>日常保持休闲节奏即可。</p><hr><h2 id="三、至高教-贡船" tabindex="-1">三、至高教（贡船） <a class="header-anchor" href="#三、至高教-贡船" aria-label="Permalink to &quot;三、至高教（贡船）&quot;">​</a></h2><p>地图缩小 → 选择至高教 六边形带刺图标（像眼睛）</p><p>消耗：</p><p>30 行动值</p><p>掉落：</p><ul><li>金属</li><li>水</li><li>食物</li><li>银河币</li><li>小概率舰船碎片</li></ul><hr><h3 id="食物收益的隐藏机制" tabindex="-1">食物收益的隐藏机制 <a class="header-anchor" href="#食物收益的隐藏机制" aria-label="Permalink to &quot;食物收益的隐藏机制&quot;">​</a></h3><p>食物获得量与：</p><p><strong>反攻行动等级挂钩</strong></p><p>进入反攻行动界面可看到：</p><ul><li>当前章节等级</li><li>声望点数</li></ul><p>声望提升后：</p><ul><li>击败至高教获得额外食物（例如 +100%）</li><li>日常任务宝箱奖励提高</li></ul><p>这属于隐藏加成机制。</p><p>很多玩家没有注意。</p><hr><h2 id="四、至高爪牙" tabindex="-1">四、至高爪牙 <a class="header-anchor" href="#四、至高爪牙" aria-label="Permalink to &quot;四、至高爪牙&quot;">​</a></h2><p>地图上正方形图标。</p><p>消耗：</p><p>10 行动值</p><p>收益低于贡船，但：</p><p>有首次击杀奖励。</p><hr><h3 id="首次击杀奖励" tabindex="-1">首次击杀奖励 <a class="header-anchor" href="#首次击杀奖励" aria-label="Permalink to &quot;首次击杀奖励&quot;">​</a></h3><p>搜索 → 拉到最高等级 → 首次击杀</p><p>奖励包括：</p><ul><li>银色英雄碎片</li><li>传奇训练手册</li></ul><p>这是系统帮助新手培养英雄的设计。</p><p>建议：</p><p>每个等级都打一次最高级。</p><hr><h2 id="五、集结机制与风险控制" tabindex="-1">五、集结机制与风险控制 <a class="header-anchor" href="#五、集结机制与风险控制" aria-label="Permalink to &quot;五、集结机制与风险控制&quot;">​</a></h2><p>打高等级贡船通常需要集结。</p><p>在商会界面：</p><p>开启自动 AI 加入</p><p>系统机制：</p><p>队友会“瞬移”加入集结 无需担心距离</p><hr><h3 id="重要技巧-打不过怎么办" tabindex="-1">重要技巧：打不过怎么办？ <a class="header-anchor" href="#重要技巧-打不过怎么办" aria-label="Permalink to &quot;重要技巧：打不过怎么办？&quot;">​</a></h3><p>很多新玩家不知道：</p><p>如果集结后发现打不过——</p><p>不要等爆船。</p><p>操作方式：</p><p>商会 → 集结界面 → 右上角点击「叉」</p><p>取消集结后：</p><p>战斗立即停止。</p><p>优点：</p><ul><li>减少行动值浪费</li><li>避免爆船</li><li>节省返航时间</li></ul><p>爆船后需重新从驻地飞过来，非常浪费时间。</p><hr><h2 id="小结" tabindex="-1">小结 <a class="header-anchor" href="#小结" aria-label="Permalink to &quot;小结&quot;">​</a></h2><p>主动资源获取结构：</p><ol><li>至高祈祷舰（免费）</li><li>贡船（高收益）</li><li>至高爪牙（首次奖励）</li><li>集结协作</li></ol><p>资源效率核心在于：</p><ul><li>打最高等级</li><li>利用隐藏加成</li><li>避免无谓损失</li></ul><p>成熟玩家和普通玩家的区别，不在战力。 在于是否理解“收益结构”。</p>',107)])])}const b=l(r,[["render",o]]);export{c as __pageData,b as default};
