import{_ as a,o as i,c as e,ag as r}from"./chunks/framework.ez65dQvE.js";const c=JSON.parse('{"title":"商會入門.md","description":"","frontmatter":{},"headers":[],"relativePath":"TraditionalChinese/發育/1.商會入門.md","filePath":"TraditionalChinese/發育/1.商會入門.md"}'),h={name:"TraditionalChinese/發育/1.商會入門.md"};function t(o,l,p,u,n,d){return i(),e("div",null,[...l[0]||(l[0]=[r('<h1 id="商會入門-md" tabindex="-1">商會入門.md <a class="header-anchor" href="#商會入門-md" aria-label="Permalink to &quot;商會入門.md&quot;">​</a></h1><hr><h1 id="商會入門" tabindex="-1">商會入門 <a class="header-anchor" href="#商會入門" aria-label="Permalink to &quot;商會入門&quot;">​</a></h1><p>商會是中長期發育的核心繫統之一。</p><p>不加入商會，等於主動放棄大量收益。</p><p>本章講：</p><ul><li>捐獻與科技</li><li>新港收益</li><li>集結與幫助</li><li>商店兌換</li><li>每週必買內容</li></ul><hr><h2 id="一、商會位置" tabindex="-1">一、商會位置 <a class="header-anchor" href="#一、商會位置" aria-label="Permalink to &quot;一、商會位置&quot;">​</a></h2><p>主界面 → 右上角 → 商會</p><p>進入後主要包含：</p><ul><li>科技</li><li>新港</li><li>集結</li><li>幫助</li><li>商店</li><li>成員</li></ul><hr><h2 id="二、科技捐獻-必須做" tabindex="-1">二、科技捐獻（必須做） <a class="header-anchor" href="#二、科技捐獻-必須做" aria-label="Permalink to &quot;二、科技捐獻（必須做）&quot;">​</a></h2><p>進入「科技」界面可以捐獻：</p><ul><li>水</li><li>金屬</li></ul><h3 id="重點結論" tabindex="-1">重點結論： <a class="header-anchor" href="#重點結論" aria-label="Permalink to &quot;重點結論：&quot;">​</a></h3><blockquote><p>捐獻一定要做。</p></blockquote><p>原因：</p><ul><li>水和金屬後期必然溢出</li><li>核心與建築升級時間極長</li><li>日常資源產出遠大於消耗</li></ul><p>如果不是瘋狂氪金衝加速：</p><p>水和金屬基本會長期積壓。</p><hr><h3 id="不要亂捐" tabindex="-1">不要亂捐 <a class="header-anchor" href="#不要亂捐" aria-label="Permalink to &quot;不要亂捐&quot;">​</a></h3><p>科技樹從上往下排列。</p><p>優先捐：</p><ul><li>帶“點贊圖標”的項目 （公會管理推薦方向）</li></ul><p>不要隨意分散捐獻。</p><hr><h3 id="科技分類" tabindex="-1">科技分類 <a class="header-anchor" href="#科技分類" aria-label="Permalink to &quot;科技分類&quot;">​</a></h3><p>一般會分爲：</p><ul><li>太空戰鬥</li><li>飛速發展</li></ul><p>具體優先方向由公會管理決定。</p><hr><h2 id="三、新港收益" tabindex="-1">三、新港收益 <a class="header-anchor" href="#三、新港收益" aria-label="Permalink to &quot;三、新港收益&quot;">​</a></h2><p>如果公會佔領星區：</p><p>每天可領取：</p><ul><li>水</li><li>金屬</li><li>銀河幣</li></ul><p>記得每日領取。</p><p>屬於穩定被動收益。</p><hr><h2 id="四、集結系統" tabindex="-1">四、集結系統 <a class="header-anchor" href="#四、集結系統" aria-label="Permalink to &quot;四、集結系統&quot;">​</a></h2><p>在商會界面可以看到：</p><ul><li>當前集結情況</li><li>活躍成員</li></ul><p>建議：</p><p>開啓自動加入。</p><p>把戰鬥力最高的艦隊放進去。</p><p>商運運輸與戰力無關， 因此可以把高戰力艦隊用於集結支援。</p><hr><h2 id="五、幫助系統" tabindex="-1">五、幫助系統 <a class="header-anchor" href="#五、幫助系統" aria-label="Permalink to &quot;五、幫助系統&quot;">​</a></h2><p>包括：</p><ul><li>科技幫助</li><li>建築幫助</li></ul><p>每次幫助都可以縮短時間。</p><p>屬於互利機制。</p><hr><h2 id="六、商店系統" tabindex="-1">六、商店系統 <a class="header-anchor" href="#六、商店系統" aria-label="Permalink to &quot;六、商店系統&quot;">​</a></h2><p>商會商店可使用：</p><ul><li>商會券</li></ul><hr><h3 id="商會券來源" tabindex="-1">商會券來源 <a class="header-anchor" href="#商會券來源" aria-label="Permalink to &quot;商會券來源&quot;">​</a></h3><ul><li>科技捐獻</li><li>商會幫助</li><li>商會禮物</li><li>至高祈禱艦（每日 2500）</li><li>其他活動獎勵</li></ul><hr><h2 id="七、前期商店購買建議" tabindex="-1">七、前期商店購買建議 <a class="header-anchor" href="#七、前期商店購買建議" aria-label="Permalink to &quot;七、前期商店購買建議&quot;">​</a></h2><p>商店中常見折扣商品（xx% off）。</p><h3 id="前期不建議" tabindex="-1">前期不建議： <a class="header-anchor" href="#前期不建議" aria-label="Permalink to &quot;前期不建議：&quot;">​</a></h3><ul><li>用商會券大量購買水和金屬</li></ul><p>原因：</p><p>這些資源後期溢出。</p><hr><h3 id="打架玩家優先購買" tabindex="-1">打架玩家優先購買： <a class="header-anchor" href="#打架玩家優先購買" aria-label="Permalink to &quot;打架玩家優先購買：&quot;">​</a></h3><ul><li>通用維修模組</li><li>傳奇訓練手冊（提升英雄技能）</li></ul><hr><h2 id="八、每週刷新內容-重點" tabindex="-1">八、每週刷新內容（重點） <a class="header-anchor" href="#八、每週刷新內容-重點" aria-label="Permalink to &quot;八、每週刷新內容（重點）&quot;">​</a></h2><p>每週刷新商品中：</p><h3 id="必買推薦" tabindex="-1">必買推薦： <a class="header-anchor" href="#必買推薦" aria-label="Permalink to &quot;必買推薦：&quot;">​</a></h3><p><strong>深空信標</strong></p><p>用途：</p><ul><li>抽取艦船工作人員</li><li>有概率獲得金色</li></ul><p>適用等級：</p><p>建議 20 級後重點考慮。</p><hr><h3 id="_20級後可關注" tabindex="-1">20級後可關注： <a class="header-anchor" href="#_20級後可關注" aria-label="Permalink to &quot;20級後可關注：&quot;">​</a></h3><ul><li>迴響模組</li><li>深空迴響</li></ul><p>用途：</p><ul><li>提升艦船等級</li></ul><p>20級以下可以忽略。</p><hr><h3 id="其他可選" tabindex="-1">其他可選： <a class="header-anchor" href="#其他可選" aria-label="Permalink to &quot;其他可選：&quot;">​</a></h3><ul><li>銀河幣</li><li>加速</li><li>奇農藥水</li></ul><p>根據個人需求決定。</p><hr><h2 id="九、成員頁面" tabindex="-1">九、成員頁面 <a class="header-anchor" href="#九、成員頁面" aria-label="Permalink to &quot;九、成員頁面&quot;">​</a></h2><p>可查看：</p><ul><li>成員在線情況</li><li>活躍狀態</li></ul><p>用於管理與協作。</p><hr><h2 id="十、管理功能" tabindex="-1">十、管理功能 <a class="header-anchor" href="#十、管理功能" aria-label="Permalink to &quot;十、管理功能&quot;">​</a></h2><p>管理員可進行：</p><ul><li>科技推薦調整</li><li>集結管理</li><li>其他內部管理</li></ul><p>（管理細節將在管理章節單獨說明。）</p><hr><h2 id="小結" tabindex="-1">小結 <a class="header-anchor" href="#小結" aria-label="Permalink to &quot;小結&quot;">​</a></h2><p>商會核心邏輯：</p><ol><li>捐獻必做</li><li>優先按推薦方向捐</li><li>商會券別亂花</li><li>深空信標優先級高</li><li>20級以下忽略高階系統</li></ol><p>商會是資源放大器。</p><p>真正強的玩家：</p><p>不是資源多。 是結構清晰。</p><hr><p>至此，你的手冊已經覆蓋：</p><ul><li>資源體系</li><li>發育邏輯</li><li>建築與旗艦</li><li>英雄系統</li><li>主線與開荒</li><li>商會系統</li></ul>',110)])])}const b=a(h,[["render",t]]);export{c as __pageData,b as default};
