import{_ as a,o as i,c as e,ag as p}from"./chunks/framework.ez65dQvE.js";const c=JSON.parse('{"title":"商會管理說明","description":"","frontmatter":{},"headers":[],"relativePath":"TraditionalChinese/發育/2.商會管理說明.md","filePath":"TraditionalChinese/發育/2.商會管理說明.md"}'),r={name:"TraditionalChinese/發育/2.商會管理說明.md"};function t(h,l,o,n,u,d){return i(),e("div",null,[...l[0]||(l[0]=[p('<h1 id="商會管理說明" tabindex="-1">商會管理說明 <a class="header-anchor" href="#商會管理說明" aria-label="Permalink to &quot;商會管理說明&quot;">​</a></h1><p>本章面向商會管理人員。</p><p>包括：</p><ul><li>標記與協作</li><li>成員管理</li><li>科技引導</li><li>管理功能</li></ul><hr><h2 id="一、商會標記" tabindex="-1">一、商會標記 <a class="header-anchor" href="#一、商會標記" aria-label="Permalink to &quot;一、商會標記&quot;">​</a></h2><p>管理層可以使用商會標記功能。</p><p>用途包括：</p><ul><li>標記攻擊目標</li><li>指定集結位置</li><li>標記資源點</li><li>戰略方向提示</li></ul><p>標記是最直接的指揮工具。</p><p>建議：</p><p>重要目標一定標記，不要只靠文字說明。</p><hr><h2 id="二、商會列表" tabindex="-1">二、商會列表 <a class="header-anchor" href="#二、商會列表" aria-label="Permalink to &quot;二、商會列表&quot;">​</a></h2><p>在商會列表中可以查看：</p><ul><li>其他商會</li><li>當前盟友</li><li>潛在對手</li></ul><p>例如：</p><ul><li>MIT（盟友）</li><li>第二UH（其他公會）</li></ul><p>用於觀察區域格局。</p><hr><h2 id="三、成員管理" tabindex="-1">三、成員管理 <a class="header-anchor" href="#三、成員管理" aria-label="Permalink to &quot;三、成員管理&quot;">​</a></h2><p>進入「成員」頁面。</p><hr><h3 id="_1-商會招募" tabindex="-1">1）商會招募 <a class="header-anchor" href="#_1-商會招募" aria-label="Permalink to &quot;1）商會招募&quot;">​</a></h3><p>可開啓或管理招募功能。</p><p>用於吸納新成員。</p><hr><h3 id="_2-捐獻排行-獎盃圖標" tabindex="-1">2）捐獻排行（獎盃圖標） <a class="header-anchor" href="#_2-捐獻排行-獎盃圖標" aria-label="Permalink to &quot;2）捐獻排行（獎盃圖標）&quot;">​</a></h3><p>點擊獎盃圖標可查看：</p><ul><li>本週成員捐獻情況</li><li>水與金屬總捐獻量</li></ul><p>每次捐獻都會增加貢獻點。</p><p>管理建議：</p><ul><li>定期查看貢獻榜</li><li>鼓勵成員參與捐獻</li><li>及時提醒未參與成員</li></ul><p>科技推進依賴集體貢獻。</p><hr><h2 id="四、管理郵件" tabindex="-1">四、管理郵件 <a class="header-anchor" href="#四、管理郵件" aria-label="Permalink to &quot;四、管理郵件&quot;">​</a></h2><p>管理者可發送郵件：</p><ul><li>給全體成員</li><li>或僅管理層</li></ul><p>郵件支持標題與正文。</p><p>建議用途：</p><ul><li>活動提醒</li><li>戰鬥安排</li><li>科技方向說明</li><li>內部通知</li></ul><hr><h2 id="五、商會管理功能" tabindex="-1">五、商會管理功能 <a class="header-anchor" href="#五、商會管理功能" aria-label="Permalink to &quot;五、商會管理功能&quot;">​</a></h2><p>管理界面中可進行：</p><ul><li>科技推薦設置</li><li>投票功能</li><li>其他高級管理操作</li></ul><p>建議：</p><p>使用“推薦科技”功能，引導成員集中捐獻。</p><p>科技集中推進，比平均推進更有效。</p><hr><h2 id="六、新港管理提醒" tabindex="-1">六、新港管理提醒 <a class="header-anchor" href="#六、新港管理提醒" aria-label="Permalink to &quot;六、新港管理提醒&quot;">​</a></h2><p>必須強調：</p><blockquote><p>商會新港必須駐紮在高等級星球。</p></blockquote><p>原因：</p><ul><li>商運收益更高</li><li>行商網絡獎勵更高</li><li>整體發育速度更快</li></ul><p>管理層應主動提醒成員遷移。</p><hr><h2 id="七、科技捐獻原則" tabindex="-1">七、科技捐獻原則 <a class="header-anchor" href="#七、科技捐獻原則" aria-label="Permalink to &quot;七、科技捐獻原則&quot;">​</a></h2><p>再次強調：</p><p>科技捐獻一定要做。</p><p>原因：</p><ul><li>水與金屬長期溢出</li><li>科技收益長期有效</li><li>捐獻回報率高</li></ul><p>管理層可通過“推薦科技”統一方向。</p><hr><h2 id="八、商會禮物機制" tabindex="-1">八、商會禮物機制 <a class="header-anchor" href="#八、商會禮物機制" aria-label="Permalink to &quot;八、商會禮物機制&quot;">​</a></h2><p>商會禮物來源：</p><ul><li>成員課金</li><li>擊敗貢船</li><li>活動獎勵</li></ul><p>擊敗貢船後：</p><p>會向全體成員發送禮物。</p><p>因此：</p><p>多打貢船，對全體都有收益。</p><hr><h3 id="關於課金" tabindex="-1">關於課金 <a class="header-anchor" href="#關於課金" aria-label="Permalink to &quot;關於課金&quot;">​</a></h3><p>課金屬於個人選擇。</p><p>管理層不鼓勵強制。</p><p>建議：</p><p>量力而爲。</p><hr><h2 id="九、宣戰與pvp" tabindex="-1">九、宣戰與PVP <a class="header-anchor" href="#九、宣戰與pvp" aria-label="Permalink to &quot;九、宣戰與PVP&quot;">​</a></h2><p>商會宣戰、區域對抗等內容：</p><p>將在 PVP 章節詳細說明。</p><hr><h2 id="小結" tabindex="-1">小結 <a class="header-anchor" href="#小結" aria-label="Permalink to &quot;小結&quot;">​</a></h2><p>商會管理核心：</p><ol><li>用標記做指揮</li><li>用推薦科技做引導</li><li>關注成員捐獻情況</li><li>統一新港駐紮方向</li><li>保持溝通透明</li></ol><p>強商會不是靠個人。</p><p>而是靠結構。</p><p>管理層的職責是：</p><p>讓結構運轉順暢。</p>',88)])])}const _=a(r,[["render",t]]);export{c as __pageData,_ as default};
