import{_ as a,o as i,c as p,ag as e}from"./chunks/framework.ez65dQvE.js";const s=JSON.parse('{"title":"建築與核心升級策略","description":"","frontmatter":{},"headers":[],"relativePath":"TraditionalChinese/發育/3.建築與核心升級策略.md","filePath":"TraditionalChinese/發育/3.建築與核心升級策略.md"}'),o={name:"TraditionalChinese/發育/3.建築與核心升級策略.md"};function r(t,l,h,u,n,c){return i(),p("div",null,[...l[0]||(l[0]=[e('<h1 id="建築與核心升級策略" tabindex="-1">建築與核心升級策略 <a class="header-anchor" href="#建築與核心升級策略" aria-label="Permalink to &quot;建築與核心升級策略&quot;">​</a></h1><p>建築系統是發育的主軸。</p><p>錯誤做法：</p><blockquote><p>所有建築平均升級。</p></blockquote><p>正確做法：</p><blockquote><p>圍繞核心推進，其他爲輔助。</p></blockquote><hr><h2 id="一、核心優先原則" tabindex="-1">一、核心優先原則 <a class="header-anchor" href="#一、核心優先原則" aria-label="Permalink to &quot;一、核心優先原則&quot;">​</a></h2><p>位置：</p><p>右下角 → 建築圖標</p><p>核心等級決定：</p><ul><li>解鎖內容</li><li>發育上限</li><li>部分系統權限</li></ul><hr><h3 id="粗暴但有效的策略" tabindex="-1">粗暴但有效的策略 <a class="header-anchor" href="#粗暴但有效的策略" aria-label="Permalink to &quot;粗暴但有效的策略&quot;">​</a></h3><p>核心升級前：</p><ul><li>只升級“必須的前置建築”</li><li>其他建築一律不管</li></ul><p>核心升級完成後：</p><ul><li>再處理下一輪前置條件</li></ul><p>這種方式效率最高。</p><hr><h3 id="短時間升級策略" tabindex="-1">短時間升級策略 <a class="header-anchor" href="#短時間升級策略" aria-label="Permalink to &quot;短時間升級策略&quot;">​</a></h3><p>如果某個建築：</p><ul><li>剛解鎖</li><li>升級時間極短（1~5分鐘）</li></ul><p>建議連續升級，直到：</p><p>升級時間達到 1~2 小時</p><p>然後停止，迴歸核心優先。</p><hr><h3 id="公會求助機制" tabindex="-1">公會求助機制 <a class="header-anchor" href="#公會求助機制" aria-label="Permalink to &quot;公會求助機制&quot;">​</a></h3><p>升級時可以點擊“求助”。</p><p>公會成員可幫助縮短時間。</p><p>因此：</p><p>短時間建築連續升級收益更高。</p><hr><h2 id="二、造船廠的特殊機制" tabindex="-1">二、造船廠的特殊機制 <a class="header-anchor" href="#二、造船廠的特殊機制" aria-label="Permalink to &quot;二、造船廠的特殊機制&quot;">​</a></h2><p>建築最左側三個：</p><ul><li>動能造船廠</li><li>光束造船廠</li><li>離子造船廠</li></ul><p>它們升級規則特殊：</p><blockquote><p>每 5 級一個質變。</p></blockquote><p>例如：</p><ul><li>24級 → 只能造 4階船</li><li>25級 → 立刻解鎖 5階船</li></ul><p>屬於階梯式突破，而不是線性增長。</p><p>如果你接近關鍵等級：</p><p>可以優先衝刺。</p><hr><h2 id="三、資源生產建築" tabindex="-1">三、資源生產建築 <a class="header-anchor" href="#三、資源生產建築" aria-label="Permalink to &quot;三、資源生產建築&quot;">​</a></h2><p>包括：</p><ul><li>水循環系統</li><li>熔鍊室</li><li>食品加工室</li></ul><p>適合：</p><ul><li>偏休閒玩家</li><li>每日上線 2~3 次領取資源</li></ul><p>活躍玩家：</p><p>不建議優先投資。</p><p>收益有限。</p><hr><h2 id="四、加速使用策略" tabindex="-1">四、加速使用策略 <a class="header-anchor" href="#四、加速使用策略" aria-label="Permalink to &quot;四、加速使用策略&quot;">​</a></h2><p>這是關鍵區別。</p><hr><h3 id="核心-15-級之前" tabindex="-1">核心 15 級之前 <a class="header-anchor" href="#核心-15-級之前" aria-label="Permalink to &quot;核心 15 級之前&quot;">​</a></h3><p>建議：</p><blockquote><p>大量使用加速。</p></blockquote><p>原因：</p><ul><li>升級時間短</li><li>加速收益高</li><li>發育節奏快</li></ul><p>萌新應優先衝等級。</p><hr><h3 id="核心-20-級之後" tabindex="-1">核心 20 級之後 <a class="header-anchor" href="#核心-20-級之後" aria-label="Permalink to &quot;核心 20 級之後&quot;">​</a></h3><p>升級時間：</p><ul><li>數天</li><li>甚至數週</li></ul><p>此時加速收益極低。</p><p>建議：</p><blockquote><p>囤積加速。</p></blockquote><p>留到：</p><ul><li>VS 活動</li><li>公會對決</li><li>衝榜活動</li></ul><p>玩法會發生轉變：</p><p>前期 → 快速成長 後期 → 節奏對抗</p><hr><h2 id="五、隱藏收益-rian" tabindex="-1">五、隱藏收益：Rian <a class="header-anchor" href="#五、隱藏收益-rian" aria-label="Permalink to &quot;五、隱藏收益：Rian&quot;">​</a></h2><p>每天在覈心建築左下角：</p><p>可以與 NPC「Rian」對話。</p><p>獎勵：</p><ul><li>50 行動值</li></ul><p>屬於隱藏收益。</p><p>建議每天領取。</p><hr><h2 id="六、其他-npc-對話獎勵" tabindex="-1">六、其他 NPC 對話獎勵 <a class="header-anchor" href="#六、其他-npc-對話獎勵" aria-label="Permalink to &quot;六、其他 NPC 對話獎勵&quot;">​</a></h2><p>部分 NPC 會出現對話選項。</p><p>點擊後可獲得小額獎勵。</p><p>建議順手領取。</p><hr><h2 id="七、升級方向總結" tabindex="-1">七、升級方向總結 <a class="header-anchor" href="#七、升級方向總結" aria-label="Permalink to &quot;七、升級方向總結&quot;">​</a></h2><p>建築升級沒有分支路線。</p><p>只有三個問題：</p><ol><li>優先升誰</li><li>是否用加速</li><li>是否衝關鍵等級</li></ol><p>核心原則：</p><ul><li>核心優先</li><li>造船廠看階梯</li><li>資源建築看活躍度</li><li>加速分階段使用</li></ul><hr><h2 id="小結" tabindex="-1">小結 <a class="header-anchor" href="#小結" aria-label="Permalink to &quot;小結&quot;">​</a></h2><p>建築發育分兩個階段：</p><p>前期：</p><blockquote><p>快速推進核心</p></blockquote><p>後期：</p><blockquote><p>控制節奏，留資源打活動</p></blockquote><p>發育不是“全部升級”。</p><p>而是“選擇性升級”。</p><p>平均主義是效率殺手。</p>',103)])])}const b=a(o,[["render",r]]);export{s as __pageData,b as default};
